// Combine all movies into master list
export const ALL_NIGHTIN_MOVIES: NightInMovie[] = [
  ...NETFLIX_MOVIES,
  ...PRIME_MOVIES,
  ...DISNEY_MOVIES,
  ...APPLE_MOVIES,
  ...SKY_MOVIES,
  ...ADDITIONAL_MOVIES,
  ...CLASSIC_MOVIES,
  ...MORE_MOVIES,
];

// Search function for night in movies
export function searchNightInMovies(
  query?: string,
  genre?: string,
  streamingService?: string,
  mood?: string,
  limit: number = 10
): NightInMovie[] {
  let movies = [...ALL_NIGHTIN_MOVIES];

  if (query) {
    const lowerQuery = query.toLowerCase();
    movies = movies.filter(m =>
      m.title.toLowerCase().includes(lowerQuery) ||
      m.synopsis.toLowerCase().includes(lowerQuery) ||
      m.director.toLowerCase().includes(lowerQuery) ||
      m.cast.some(c => c.toLowerCase().includes(lowerQuery))
    );
  }

  if (genre) {
    const lowerGenre = genre.toLowerCase();
    movies = movies.filter(m =>
      m.genres.some(g => g.toLowerCase().includes(lowerGenre))
    );
  }

  if (streamingService) {
    const lowerService = streamingService.toLowerCase();
    movies = movies.filter(m =>
      m.streamingServices.some(s => s.toLowerCase().includes(lowerService))
    );
  }

  if (mood) {
    const lowerMood = mood.toLowerCase();
    movies = movies.filter(m =>
      m.mood.some(mo => mo.toLowerCase().includes(lowerMood))
    );
  }

  // Sort by IMDB score descending
  movies.sort((a, b) => b.imdbScore - a.imdbScore);

  return movies.slice(0, limit);
}

// Get unique streaming services
export function getStreamingServices(): string[] {
  const services = new Set<string>();
  ALL_NIGHTIN_MOVIES.forEach(m => {
    m.streamingServices.forEach(s => services.add(s));
  });
  return Array.from(services).sort();
}

// Get unique genres
export function getGenres(): string[] {
  const genres = new Set<string>();
  ALL_NIGHTIN_MOVIES.forEach(m => {
    m.genres.forEach(g => genres.add(g));
  });
  return Array.from(genres).sort();
}

// Get unique moods
export function getMoods(): string[] {
  const moods = new Set<string>();
  ALL_NIGHTIN_MOVIES.forEach(m => {
    m.mood.forEach(mo => moods.add(mo));
  });
  return Array.from(moods).sort();
}
